document.querySelectorAll(".sixth-screen__accordion-button").forEach(t=>{t.addEventListener("click",()=>{let e=t.nextElementSibling;e.style.maxHeight?document.querySelectorAll(".sixth-screen__accordion-content").forEach(e=>{e.style.maxHeight=null}):(document.querySelectorAll(".sixth-screen__accordion-content").forEach(e=>{e.style.maxHeight=null}),e.style.maxHeight=e.scrollHeight+"px")})});