function formatNumberWithCommas(t){return t.toString().replace(/\B(?=(\d{3})+(?!\d))/g,".")}function animateValue(r,a,t,o){const m=t-a,u=performance.now();requestAnimationFrame(function t(e){var e=e-u,e=Math.min(e/o,1),n=Math.floor(e*m+a);r.textContent=formatNumberWithCommas(n),e<1&&requestAnimationFrame(t)})}const counterElement=document.querySelectorAll(".counter");counterElement.forEach(t=>{var e=t.getAttribute("data-end");animateValue(t,0,+e,2e3)});