function formatNumberWithCommas(t){return t.toString().replace(/\B(?=(\d{3})+(?!\d))/g,".")}function animateValue(o,r,t,a){const m=t-r,u=performance.now();requestAnimationFrame(function t(e){e-=u,e=Math.min(e/a,1);const n=e*m+r;o.textContent=formatNumberWithCommas(n.toFixed(1)),e<1&&requestAnimationFrame(t)})}const counterElement=document.querySelectorAll(".counter");counterElement.forEach(t=>{var e=t.getAttribute("data-end");animateValue(t,0,+e,2e3)});